
public class BoxingnUnboxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int i1=10;
         Integer i2=new Integer(i1);
         //boxing
         int i3=i2.intValue();
         //unboxing
         
         double d1=10;
         Double d2=new Double(i1);
         //boxing
         double d3=d2.doubleValue();
         //unboxing
         
         
         char  c1=10;
         Character c2=new Character(c1);
         //boxing
         char c3=c2.charValue();
         //unboxing
         
         float f1=10;
          Float f2=new Float(f1);
         //boxing
         float
         f3=f2.floatValue();
         //unboxing
	}

}
